<script>
import { getTruckDetails, getlicenseDetails } from '@/api/modules/vehicle/vehicles'
export default {
  data() {
    return {
      activeName: 'baseInfo',
      baseInfoData: {},
      licenseData: {},
      // 页面进来获取id
      id: this.$route.params.id,
      rules: {
        allowableLoad: [{ required: true, message: '请输入车辆载重' }],
        licensePlate: [{ required: true, message: '请输入车牌号码' }],
        deviceGpsId: [{ required: true, message: '请输入GPS设备ID' }],
        truckTypeName: [{ required: true, message: '请输入车辆名称' }],
        allowableVolume: [{ required: true, message: '请输入车牌体积' }]
      },
      // 用于判断是展示input还是文字
      flag: false,
      // 存放基本信息图片数组
      basicInfoImgList: [],
      // 存放行驶证图片数组
      licenseImgList: []
    }
  },
  created() {
    this.getBasicInfo()
    this.licenseDetails()
  },
  methods: {
    handleClick() {},
    // 获取基本详情
    async getBasicInfo() {
      const { data } = await getTruckDetails(this.id)
      this.baseInfoData = data
      // 因为后端返回过来的图片是字符串形式 并且两张图片是连在一起的,所以需要做处理
      if (data.picture) {
        // 将图片格式进行分割重组
        this.basicInfoImgList = data.picture.split(',').map((item) => {
          return { url: item }
        })
      }
    },
    // 获取行驶证详情
    async licenseDetails() {
      const { data } = await getlicenseDetails(this.id)
      this.licenseData = data
      console.log(data)
      // 处理图片格式
      if (data.picture) {
        this.licenseImgList = data.picture.split(',').map(item => {
          return { url: item }
        })
      }
      console.log(this.licenseImgList)
    }

  }
}
</script>

